model.beyond_basics.stg_public_datasets_.raw_code:
{{
    config(
        materialized = 'incremental',
        incremental_strategy = 'insert_overwrite',
        partition_by = {'data_type': 'date', 'field': 'timestamp_date', 'granularity': 'day'}
    )
}}

select distinct
    `hash` as block_hash,
    timestamp as created_at,
    date(timestamp) as timestamp_date,
    * except (`hash`, timestamp, timestamp_month)
from {{ source('crypto_bitcoin', 'blocks') }}
where
    timestamp <= timestamp_trunc(current_timestamp(), day)

    {% if is_incremental() %}

        and timestamp >= timestamp(date_sub(current_date(), interval 1 day))

    {% endif %}

    {# minimize data usage for dev #}
    {% if not target.name in ['stg', 'prod'] or env_var('DBT_CICD_RUN', 'false') == 'true' %}

        and timestamp_month
        >= date_trunc(date_sub(current_date(), interval 1 month), month)

    {% endif %}

    and timestamp_month >= "2020-01-01"

model.beyond_basics.int_orders.raw_code:
{{
    config(
        persist_docs={"relation": true, "columns": false}
    )

}}

{% set payment_methods = ['credit_card', 'coupon', 'bank_transfer', 'gift_card'] %}

with
    orders as (select * from {{ ref('stg_jaffle_shop__orders') }}),

    payments as (select * from {{ ref('stg_stripe__payments') }}),

    order_payments as (

        select
            order_id,

            {% for payment_method in payment_methods -%}
                sum(
                    case
                        when payment_method = '{{ payment_method }}' then amount else 0
                    end
                ) as {{ payment_method }}_amount,
            {% endfor -%}

            sum(amount) as total_amount

        from payments

        group by order_id

    ),

    final as (

        select
            orders.order_id,
            orders.customer_id,
            orders.order_date,
            orders.status,

            {% for payment_method in payment_methods -%}

                order_payments.{{ payment_method }}_amount,

            {% endfor -%}

            order_payments.total_amount as amount

        from orders

        left join order_payments on orders.order_id = order_payments.order_id

    )

select *
from final

